package ch.algotrader.strategy.xyz;

@ManagedResource(objectName = "ch.algotrader.strategy.xyz:name=XYZChart,type=chart")
public class MovChartService extends ChartProvidingServiceImpl {

  @Override
  public Collection<IndicatorVO> getIndicators(long startDateTime) {

    List<MarketDataEventVO> marketDataEvents = EsperManager.executeQuery("MOV",
      "select * from CurrentValueWindow where dateTime.toMillisec() > " + startDateTime);

    Set<IndicatorVO> set = new HashSet<IndicatorVO>();
    for (MarketDataEventVO marketDataEvent : marketDataEvents) {
      set.add(new IndicatorVO("currentValue", marketDataEvent.getDateTime(),
        marketDataEvent.getCurrentValue().doubleValue()));
    }

    return set;
  }

  @Override
  public Collection<MarkerVO> getMarkers() {

    Double target = (Double) EsperManager.getVariableValue("MOV", "target");

    return Collections.singleton(new ValueMarkerVO("target", target))
  }

  @Override
  public String getDescription() {

    Double stop = (Double) EsperManager.getVariableValue("MOV", "stop");

    return String.valueOf(stop);
  }
}

public class MovService {

  public void openPosition(String strategyName, int securityId, BigDecimal price) {

   Strategy strategy = getLookupService().getStrategyByNameFetched(strategyName);
   Security security = getLookupService().getSecurity(securityId);

   int qty = (int) (getPortfolioService().getAvailableFundsDouble(strategyName) / price.doubleValue());

   if (qty <= 0) { return; }

   Order order = new MarketOrder.Factory.newInstance();
   order.setSecurity(security);
   order.setStrategy(strategy);
   order.setQuantity(qty);
   order.setSide(Side.BUY);

   getOrderService().sendOrder(order); 
  }
}

public class MovStarter {

  public static void main(String[] args) {

    ServiceLocator.instance().init(ServiceLocator.CLIENT_BEAN_REFERENCE_LOCATION); 

    MovService movService = ServiceLocator.instance().
      getService("movService", MovService.class); 

    String strategyName = movService.getStrategyName();

    EsperManager.initServiceProvider(strategyName); 

    EsperManager.deployInitModules(strategyName); 

    movService.prefeedTicks(); 

    EsperManager.setInternalClock(strategyName, true); 

    EsperManager.deployRunModules(strategyName); 

    SubscriptionService subscriptionService = ServiceLocator.instance().getSubscriptionService();
    subscriptionService.initMarketDataEventSubscriptions(); 
  }
}

public class MovStarter {

  public static void main(String[] args) extends BaseStarter {

    ServiceLocator.instance().init(ServiceLocator.SINGLE_BEAN_REFERENCE_LOCATION); 

    startBase(); 

    MovService movService = ServiceLocator.instance().
      getService("movService", MovService.class); 

    String strategyName = movService.getStrategyName();

    EsperManager.initServiceProvider(strategyName); 

    EsperManager.deployInitModules(strategyName); 

    movService.prefeedTicks(); 

    EsperManager.setInternalClock(strategyName, true); 

    EsperManager.deployRunModules(strategyName); 
  }
}

