row.field:
insert into KFast
select security,
(select (last(currentValueDouble) - min(currentValueDouble))/(max(currentValueDouble) - min(currentValueDouble)) as call
from Tick(security.isin = engineStrategy.underlaying.isin).win:length(cast(callKFastDays * simulation_eventsPerDay, int))),
(select (last(currentValueDouble) - min(currentValueDouble))/(max(currentValueDouble) - min(currentValueDouble)) as put
from Tick(security.isin = engineStrategy.underlaying.isin).win:length(cast(putKFastDays * simulation_eventsPerDay, int)))
from Tick(security.isin = engineStrategy.underlaying.isin)
having count(id) >= cast(Math.max(callKFastDays,putKFastDays) * simulation_eventsPerDay ,int)
